import React, { useState } from "react";

function Dashboard() {
  const [balance, setBalance] = useState(5000); // starting dummy balance
  const [transactions, setTransactions] = useState([]);
  const [amount, setAmount] = useState("");
  const [note, setNote] = useState("");

  const handleTransaction = (type) => {
    const amt = parseFloat(amount);
    if (isNaN(amt) || amt <= 0) {
      alert("Please enter a valid amount");
      return;
    }

    if (type === "send" && amt > balance) {
      alert("Insufficient balance");
      return;
    }

    const newBalance = type === "send" ? balance - amt : balance + amt;

    const newTransaction = {
      id: Date.now(),
      type,
      amount: amt,
      note: note || (type === "send" ? "Sent Money" : "Received Money"),
      date: new Date().toLocaleString(),
    };

    setBalance(newBalance);
    setTransactions([newTransaction, ...transactions]);
    setAmount("");
    setNote("");
  };

  return (
    <div className="min-h-screen bg-purple-100 p-4">
      {/* Header */}
      <div className="bg-purple-600 text-white p-4 rounded-2xl shadow-md mb-6">
        <h1 className="text-xl font-bold">Payzz Dashboard</h1>
        <p className="text-lg mt-1">Balance: KES {balance.toFixed(2)}</p>
      </div>

      {/* Transaction input */}
      <div className="bg-white p-4 rounded-2xl shadow-md mb-6">
        <input
          type="number"
          placeholder="Enter amount"
          value={amount}
          onChange={(e) => setAmount(e.target.value)}
          className="w-full p-2 mb-3 border rounded-lg focus:outline-none"
        />
        <input
          type="text"
          placeholder="Note (optional)"
          value={note}
          onChange={(e) => setNote(e.target.value)}
          className="w-full p-2 mb-3 border rounded-lg focus:outline-none"
        />
        <div className="flex justify-between">
          <button
            onClick={() => handleTransaction("send")}
            className="bg-red-500 text-white px-4 py-2 rounded-lg shadow hover:bg-red-600"
          >
            Send
          </button>
          <button
            onClick={() => handleTransaction("receive")}
            className="bg-green-500 text-white px-4 py-2 rounded-lg shadow hover:bg-green-600"
          >
            Receive
          </button>
        </div>
      </div>

      {/* Transaction history */}
      <div className="bg-white p-4 rounded-2xl shadow-md">
        <h2 className="text-lg font-bold mb-3">Transaction History</h2>
        {transactions.length === 0 ? (
          <p className="text-gray-500">No transactions yet</p>
        ) : (
          <ul className="space-y-3">
            {transactions.map((tx) => (
              <li
                key={tx.id}
                className="p-3 rounded-lg border flex justify-between items-center"
              >
                <div>
                  <p className="font-semibold">
                    {tx.type === "send" ? "Sent" : "Received"}: KES{" "}
                    {tx.amount.toFixed(2)}
                  </p>
                  <p className="text-sm text-gray-500">{tx.note}</p>
                  <p className="text-xs text-gray-400">{tx.date}</p>
                </div>
                <span
                  className={`px-3 py-1 rounded-lg text-white text-sm ${
                    tx.type === "send" ? "bg-red-500" : "bg-green-500"
                  }`}
                >
                  {tx.type.toUpperCase()}
                </span>
              </li>
            ))}
          </ul>
        )}
      </div>
    </div>
  );
}

export default Dashboard;
