import React, { useState } from "react";

function Dashboard() {
  const [balance, setBalance] = useState(1200);
  const [transactions, setTransactions] = useState([
    { id: 1, type: "send", amount: -50, date: "2025-08-10" },
    { id: 2, type: "receive", amount: 100, date: "2025-08-12" },
  ]);

  // Simple formatter
  const currency = (amount) =>
    `KES ${amount.toFixed(2).replace(/\B(?=(\d{3})+(?!\d))/g, ",")}`;

  // Add a dummy transaction
  const handleTx = (type) => {
    const amt = type === "send" ? -20 : 50;
    const newTx = {
      id: transactions.length + 1,
      type,
      amount: amt,
      date: new Date().toISOString().split("T")[0],
    };
    setTransactions([newTx, ...transactions]);
    setBalance((prev) => prev + amt);
  };

  // Example value for monthly spending
  const spentThisMonth = 200;

  return (
    <div className="flex flex-col min-h-screen bg-gray-100 p-4">
      {/* Balance */}
      <div className="text-center mb-6">
        <h1 className="text-xl font-bold">Your Balance</h1>
        <p className="text-3xl font-bold text-green-600">{currency(balance)}</p>
      </div>

      {/* Quick Actions */}
      <div className="flex justify-around mb-6">
        <button
          onClick={() => handleTx("send")}
          className="bg-red-500 text-white px-4 py-2 rounded-lg"
        >
          Send
        </button>
        <button
          onClick={() => handleTx("receive")}
          className="bg-green-500 text-white px-4 py-2 rounded-lg"
        >
          Receive
        </button>
      </div>

      {/* Transaction History */}
      <div className="flex-1 mb-16">
        <h2 className="text-lg font-semibold mb-2">Transaction History</h2>
        <ul className="space-y-2">
          {transactions.map((tx) => (
            <li
              key={tx.id}
              className="flex justify-between bg-white p-2 rounded shadow"
            >
              <span>{tx.type}</span>
              <span
                className={`${
                  tx.amount < 0 ? "text-red-500" : "text-green-500"
                }`}
              >
                {currency(tx.amount)}
              </span>
              <span>{tx.date}</span>
            </li>
          ))}
        </ul>
      </div>

      {/* Bottom Navigation */}
      <nav className="fixed bottom-0 left-0 right-0 bg-white border-t flex justify-around py-2">
        <button className="flex flex-col items-center text-blue-500">
          <span>🏠</span>
          <span className="text-xs">Home</span>
        </button>
        <button className="flex flex-col items-center">
          <span>📜</span>
          <span className="text-xs">Transactions</span>
        </button>
        <button className="flex flex-col items-center">
          <span>👤</span>
          <span className="text-xs">Profile</span>
        </button>
      </nav>
    </div>
  );
}

export default Dashboard;
