import React, { useMemo, useState } from "react";
import { ArrowDownCircle, ArrowUpCircle } from "lucide-react";

const currency = (n: number) => `KES ${Number(n).toFixed(2)}`;

export default function Dashboard() {
  const [balance, setBalance] = useState(4942.0);
  const [amount, setAmount] = useState("");
  const [transactions, setTransactions] = useState([
    { id: 1, type: "receive", amount: 120.5, date: new Date().toLocaleString() },
    { id: 2, type: "send", amount: 45.0, date: new Date().toLocaleString() },
  ]);

  const spentThisMonth = useMemo(
    () => transactions.filter((t) => t.type === "send").reduce((sum, t) => sum + t.amount, 0),
    [transactions]
  );

  const handleTx = (type: "send" | "receive") => {
    const amt = parseFloat(amount);
    if (isNaN(amt) || amt <= 0) {
      alert("Enter a valid amount");
      return;
    }
    if (type === "send" && amt > balance) {
      alert("Insufficient balance");
      return;
    }
    setBalance((b) => (type === "send" ? b - amt : b + amt));
    setTransactions((list) => [
      { id: Date.now(), type, amount: amt, date: new Date().toLocaleString() },
      ...list,
    ]);
    setAmount("");
  };

  return (
    <div className="min-h-screen flex flex-col bg-gradient-to-b from-gray-50 to-gray-100">
      {/* Header */}
      <header className="p-5 flex items-center justify-between bg-white shadow-md sticky top-0 z-10">
        <h1 className="text-{9xl font-extrabold text-indigo-600 tracking-tight">Loyalty Wallet</h1>
        <button className="p-2 rounded-full hover:bg-gray-100 transition">
          <svg xmlns="http://www.w3.org/2000/svg" className="w-6 h-6 text-gray-700" fill="none" stroke="currentColor" strokeWidth="2">
            <path d="M7 3H3v4M17 3h4v4M21 17v4h-4M3 17v4h4" />
          </svg>
        </button>
      </header>

      {/* Main */}
      <main className="flex-1 overflow-y-auto px-5 pb-28">
        {/* Balance Card */}
        <section className="mt-6">
          <div className="bg-gradient-to-r from-indigo-600 to-purple-600 rounded-3xl p-6 text-white shadow-xl transform hover:scale-[1.01] transition">
            <div className="flex justify-between items-center">
              <span className="bg-white/90 text-black px-3 py-1 text-xs font-medium rounded-full shadow">
                Master Card ‚ñæ
              </span>
              <span className="opacity-80 text-sm">Active Wallet</span>
            </div>
            <div className="mt-6">
              <p className="text-sm opacity-80">Available Balance</p>
              <p className="text-4xl font-extrabold">{currency(balance)}</p>
            </div>
            <div className="mt-6 flex justify-between text-xs opacity-80">
              <p>Exp. 08/26</p>
              <p>Card 5679‚Ä¢‚Ä¢‚Ä¢‚Ä¢</p>
            </div>
          </div>
        </section>

        {/* Transaction Actions */}
        <section className="mt-8">
          <h3 className="text-md font-semibold text-gray-800 mb-3">Make a Transaction</h3>
          <div className="flex gap-2 mb-2">
            <input
              type="number"
              value={amount}
              onChange={(e) => setAmount(e.target.value)}
              placeholder="Enter Amount"
              className="flex-1 border border-gray-300 rounded-xl px-4 py-2 shadow-sm focus:ring-2 focus:ring-indigo-500 outline-none"
            />
          </div>
          <div className="flex gap-3">
            <button
              onClick={() => handleTx("send")}
              className="flex-1 bg-red-500 hover:bg-red-600 text-white px-4 py-3 rounded-xl shadow-md font-semibold transition"
            >
              Send
            </button>
            <button
              onClick={() => handleTx("receive")}
              className="flex-1 bg-green-500 hover:bg-green-600 text-white px-4 py-3 rounded-xl shadow-md font-semibold transition"
            >
              Receive
            </button>
          </div>
          <p className="mt-2 text-sm text-gray-500">üí∏ Spent this month: {currency(spentThisMonth)}</p>
        </section>

        {/* Budgets
        <section className="mt-10">
          <h2 className="text-lg font-bold text-gray-800">Budgets</h2>
          <div className="space-y-4 mt-3">
            <BudgetRow label="Transportation" left="KES 678 left" percent={86} />
            <BudgetRow label="Groceries" left="KES 326 left" percent={67} />
          </div>
        </section> */}

        {/* History */}
        <section className="mt-10">
          <h3 className="text-md font-bold text-gray-800 mb-4">Transaction History</h3>
          <ul className="space-y-3">
            {transactions.map((t) => (
              <li
                key={t.id}
                className="flex items-center justify-between bg-white p-4 rounded-xl shadow hover:shadow-md transition"
              >
                <div className="flex items-center gap-3">
                  <div
                    className={`w-10 h-10 rounded-full grid place-items-center ${
                      t.type === "send" ? "bg-red-100 text-red-600" : "bg-green-100 text-green-600"
                    }`}
                  >
                    {t.type === "send" ? <ArrowUpCircle size={22} /> : <ArrowDownCircle size={22} />}
                  </div>
                  <div>
                    <div className="font-semibold text-gray-800">
                      {t.type === "send" ? "Sent" : "Received"} {currency(t.amount)}
                    </div>
                    <div className="text-xs text-gray-500">{t.date}</div>
                  </div>
                </div>
              </li>
            ))}
            {transactions.length === 0 && <p className="text-sm text-gray-400">No transactions yet</p>}
          </ul>
        </section>
      </main>

      {/* Bottom Navigation */}
      <nav className="h-20 bg-white border-t flex items-center justify-around fixed bottom-0 left-0 right-0 shadow-md">
        <button className="flex flex-col items-center text-gray-500 hover:text-indigo-600 transition">
          <span className="text-lg">üí≥</span>
          <span className="text-xs font-medium">Cards</span>
        </button>
        <button className="flex flex-col items-center">
          <div className="h-14 w-14 bg-indigo-600 hover:bg-indigo-700 rounded-full flex items-center justify-center shadow-lg text-white transition">
            ‚åÅ
          </div>
        </button>
        <button className="flex flex-col items-center text-gray-500 hover:text-indigo-600 transition">
          <span className="text-lg">üë§</span>
          <span className="text-xs font-medium">Account</span>
        </button>
      </nav>
    </div>
  );
}

function BudgetRow({ label, left, percent }: { label: string; left: string; percent: number }) {
  return (
    <div className="bg-white rounded-2xl p-5 shadow hover:shadow-md transition">
      <div className="flex items-center justify-between">
        <div>
          <p className="font-semibold text-gray-800">{label}</p>
          <p className="text-sm text-gray-500">{left}</p>
        </div>
        <p className="text-lg font-bold text-indigo-600">{percent}%</p>
      </div>
      <div className="mt-3 h-2 bg-gray-200 rounded-full overflow-hidden">
        <div className="h-full bg-indigo-600 rounded-full transition-all" style={{ width: `${percent}%` }} />
      </div>
    </div>
  );
}

export { Dashboard, currency };
